CREATE SEQUENCE review_seq;

CREATE TABLE item_review (

username VARCHAR(100) NOT NULL,
comment VARCHAR(500),
item_id INT NOT NULL,
comment_date TIMESTAMP(0) NOT NULL,
id INT NOT NULL DEFAULT NEXTVAL ('review_seq') , PRIMARY KEY (id)) ;

